const mongoose = require("mongoose");

const ListSchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
  },
  filePath: {
    type: String,
    required: true,
  },
  totalRecords: {
    type: Number,
    required: true,
  },
  distributedTo: [
    {
      agent: {
        type: mongoose.Schema.Types.ObjectId,
        ref: "Agent",
      },
      recordCount: {
        type: Number,
        required: true,
      },
    },
  ],
  createdAt: {
    type: Date,
    default: Date.now,
  },
});

module.exports = mongoose.model("List", ListSchema);
